mapper.select:

	select * from 
(select RANK() OVER( order by ref desc, step asc) no, r.*
	from (select * from review) r where bookNum = #{n})
	where no between #{s} and #{e}

select trunc(avg(rating)/10,1) from review 
where rating !=0 and
	  bookNum=#{num}

mapper.update:
update review set content=#{content},
				  regdate=sysdate
where reviewNum= #{reviewNum}
